import React, { useState } from 'react';
import './SearchBar.css';
import { useNavigate } from 'react-router-dom';


const SearchBar = () => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate(); // Hook pour la navigation


  // Requêtes prédéfinies
  const predefinedQueries = [
    'Learn React',
    'JavaScript Tutorials',
    'Educational Videos',
    'Science Projects',
    'Mathematics Resources',
  ];

  const handleSearch = () => {
    if (query.trim()) {
      navigate(`/results?query=${encodeURIComponent(query)}`);
    }
  };
  const handlePredefinedQueryClick = (predefinedQuery) => {
    setQuery(predefinedQuery);
  };

  return (
    <div className="search-container">
      {/* Grand titre */}
      <h1 className="main-title">What are you looking for ? </h1>

      {/* Requêtes prédéfinies */}
      <div className="predefined-queries">
        {predefinedQueries.map((item, index) => (
          <button
            key={index}
            onClick={() => handlePredefinedQueryClick(item)}
            className="predefined-query-btn"
          >
            {item}
          </button>
        ))}
      </div>

      {/* Barre de recherche */}
      <div className="search-bar">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="search-input"
        placeholder="Search for education"
      />
       <button onClick={handleSearch} className="search-button">
    <svg
      xmlns="http://www.w3.org/2000/svg"
      height="16"
      viewBox="0 0 24 24"
      width="16"
      fill="white"
    >
      <path d="M0 0h24v24H0V0z" fill="none" />
      <path d="M15.5 14h-.79l-.28-.27a6.471 6.471 0 001.48-5.34C15.04 5.51 12.52 3 9.5 3S4 5.51 4 8.5 6.52 14 9.5 14c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l4.25 4.25c.41.41 1.08.41 1.49 0 .41-.41.41-1.08 0-1.49L15.5 14zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z" />
    </svg>
  </button>
    </div>
    </div>
  );
};

export default SearchBar;
