import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Document, Page } from 'react-pdf'; // Assurez-vous que react-pdf est installé
import './SearchResults.css';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState(null);

  // Récupère les résultats de la recherche depuis l'état de React Router
  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
    } else {
      navigate('/');
    }
  }, [location.state, navigate]);

  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      <h2>Search Results for: {location.state?.query}</h2>

      <div className="results-section">
        {/* Section des résultats PDF */}
        <div className="results-column">
          <h3>PDF Results</h3>
          <ul>
            {results.pdf_results.map((result, index) => (
              <li key={index} className="pdf-result-item">
                <a
                  href={result.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  download={result.title} // Permet de télécharger le PDF
                  className="pdf-preview-link"
                >
                  {/* Aperçu du PDF */}
                  <div className="pdf-preview">
                    <Document file={result.link}>
                      <Page pageNumber={1} width={100} /> {/* Affichage de la première page */}
                    </Document>
                  </div>
                  <span>{result.title}</span> {/* Affichage du titre */}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Section des résultats vidéo */}
        <div className="results-column">
          <h3>Video Results</h3>
          <ul>
            {results.video_results.map((video, index) => (
              <li key={index}>
                <a href={video.link} target="_blank" rel="noopener noreferrer">
                  {video.title}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Section des résultats d'images */}
        <div className="results-column">
          <h3>Image Results</h3>
          <div className="image-grid">
            {results.image_data.map((image, index) => (
              <div key={index} className="image-result">
                <img
                  src={`data:image/jpeg;base64,${image.base64}`}
                  alt={image.file_name}
                  className="result-image"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchResults;
