import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SearchResults.css';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState({
    pdf_results: [],
    video_results: [],
    image_keywords: {},
  });
  const [query, setQuery] = useState(location.state?.query || ''); // Initial search query
  const [searchTerm, setSearchTerm] = useState(''); // Current search term for a new search

  // Handle new search submission
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      try {
        const youtubeApiKey = 'AIzaSyCJsY2i6l0-nYeaNNMCq2ubdhmCSMjQqic'; // Replace with your real API key
        const response = await fetch(`http://localhost:5000/process`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            query: searchTerm,
            youtube_api_key: youtubeApiKey,
            image_dir: "images/", // Add this if necessary
          }),
        });

        const data = await response.json();

        // Update results and navigate to the same page
        navigate('/results', { state: { query: searchTerm, searchResults: data } });
        console.log('Search results:', data);
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    }
  };

  // Load results from location state
  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
    } else {
      navigate('/'); // Redirect to home if no results are available
    }
  }, [location.state, navigate]);

  // If results are not yet available, display "Loading..."
  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      {/* Search bar for new search */}
      <div className="search-bar-container">
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            type="text"
            placeholder="Search again..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
      </div>

      <h2>{query ? `Search Results for: ${query}` : 'Search Results'}</h2>

      <div className="results-section">
        {/* PDF Results */}
        <div className="results-column">
          <h3>PDF Results</h3>
          <ul className="pdf-list">
            {results.pdf_results && results.pdf_results.length > 0 ? (
              results.pdf_results.map((result, index) => (
                <li key={index} className="pdf-result-item">
                  <a href={result.link} target="_blank" rel="noopener noreferrer" className="pdf-link">
                    <span className="pdf-icon">ðŸ“„</span>
                    {result.title} - {result.authors}
                  </a>
                </li>
              ))
            ) : (
              <p>No PDF results found.</p>
            )}
          </ul>
        </div>

        {/* Video Results */}
        <div className="results-column">
          <h3>Video Results</h3>
          <div className="video-grid">
            {results.video_results && results.video_results.length > 0 ? (
              results.video_results.map((video, index) => (
                <div key={index} className="video-item">
                  <a href={video.link} target="_blank" rel="noopener noreferrer">
                    <img src={video.thumbnail} alt={video.title} className="video-thumbnail" />
                  </a>
                  <p>{video.title}</p>
                </div>
              ))
            ) : (
              <p>No video results found.</p>
            )}
          </div>
        </div>

        {/* Image Results */}
        <div className="results-column">
          <h3>Image Results</h3>
          <div className="image-grid">
            {results.image_keywords && Object.keys(results.image_keywords).length > 0 ? (
              Object.keys(results.image_keywords).map((imageFile, index) => {
                const imageData = results.image_keywords[imageFile];
                return (
                  <div key={index} className="image-result">
                    <img
                      src={imageData.image_url} // Use direct URL if available
                      alt={imageFile}
                      className="result-image"
                    />
                    <p>Keywords: {imageData.keywords.join(", ")}</p>
                  </div>
                );
              })
            ) : (
              <p>No image results found.</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchResults;
