import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SearchResults.css';
import { GlobalWorkerOptions } from 'react-pdf';
import { Document, Page } from 'react-pdf';

// Spécifier l'emplacement du worker
GlobalWorkerOptions.workerSrc = '//cdnjs.cloudflare.com/ajax/libs/pdf.js/2.10.377/pdf.worker.min.js';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState(null);

  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
    } else {
      navigate('/');
    }
  }, [location.state, navigate]);

  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      <h2>Search Results for: {location.state?.query}</h2>

      <div className="results-section">
        {/* PDF Results */}
        <div className="results-column">
          <h3>PDF Results</h3>
          <ul>
            {results.pdf_results.map((result, index) => (
              <li key={index}>
                {/* Affichage du PDF en ligne */}
                <Document file={result.link}>
                  <Page pageNumber={1} />
                </Document>
                <a href={result.link} target="_blank" rel="noopener noreferrer">
                  {result.title} - {result.authors}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Video Results */}
        <div className="results-column">
          <h3>Video Results</h3>
          <ul>
            {results.video_results.map((video, index) => (
              <li key={index}>
                <a href={video.link} target="_blank" rel="noopener noreferrer">
                  {video.title}
                </a>
              </li>
            ))}
          </ul>
        </div>

        {/* Image Results */}
        <div className="results-column">
          <h3>Image Results</h3>
          <div className="image-grid">
            {results.image_data.map((image, index) => (
              <div key={index} className="image-result">
                <img
                  src={`data:image/jpeg;base64,${image.base64}`}
                  alt={image.file_name}
                  className="result-image"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchResults;
