import React, { useState } from 'react';
import './SearchBar.css';
import { useNavigate } from 'react-router-dom';


const SearchBar = () => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate(); // Hook pour la navigation


  // Requêtes prédéfinies
  const predefinedQueries = [
    'Learn React',
    'JavaScript Tutorials',
    'Educational Videos',
    'Science Projects',
    'Mathematics Resources',
  ];

  const handleSearch = () => {
    if (query.trim()) {
      navigate(`/results?query=${encodeURIComponent(query)}`);
    }
  };
  const handlePredefinedQueryClick = (predefinedQuery) => {
    setQuery(predefinedQuery);
  };

  return (
    <div className="search-container">
      {/* Grand titre */}
      <h1 className="main-title">What are you looking for ? </h1>

      {/* Requêtes prédéfinies */}
      <div className="predefined-queries">
        {predefinedQueries.map((item, index) => (
          <button
            key={index}
            onClick={() => handlePredefinedQueryClick(item)}
            className="predefined-query-btn"
          >
            {item}
          </button>
        ))}
      </div>

      {/* Barre de recherche */}
      <div className="search-bar">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        className="search-input"
        placeholder="Search for education"
      />
       <button onClick={handleSearch} className="search-button">
       <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" class="size-6">
  <path stroke-linecap="round" stroke-linejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z" />
</svg>

  </button>
    </div>
    </div>
  );
};

export default SearchBar;
