import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SearchResults.css';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState(null);
  const [query, setQuery] = useState(location.state?.query || ''); // To store search query
  const [searchTerm, setSearchTerm] = useState(''); // To capture new search

  // Handle form submission for new search
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (searchTerm) {
      navigate('/search', { state: { query: searchTerm } }); // Naviguer vers la page de recherche avec le nouveau terme
    }
  };

  // Retrieve search query and results from the state passed via React Router
  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
    } else {
      navigate('/');
    }
  }, [location.state, navigate]);

  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      {/* Nouvelle barre de recherche */}
      <div className="search-bar-container">
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            type="text"
            placeholder="Search again..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
      </div>

      <h2>Search Results for: {query}</h2>

      <div className="results-section">
        {/* PDF Results */}
        <div className="results-column">
          <h3>PDF Results</h3>
          <ul>
            {results.pdf_results.map((result, index) => (
              <li key={index} className="pdf-result-item">
                <a href={result.link} target="_blank" rel="noopener noreferrer" className="pdf-link">
                  {result.title} - {result.authors}
                </a>
                <span>{result.size} | {result.page_count} pages</span>
              </li>
            ))}
          </ul>
        </div>

        {/* Video Results */}
        <div className="results-column">
          <h3>Video Results</h3>
          <div className="video-grid">
            {results.video_results.map((video, index) => (
              <div key={index} className="video-item">
                <a href={video.link} target="_blank" rel="noopener noreferrer">
                  <img 
                    src={video.thumbnail} 
                    alt={video.title} 
                    className="video-thumbnail"
                  />
                </a>
                <p>{video.title}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Image Results */}
        <div className="results-column">
          <h3>Image Results</h3>
          <div className="image-grid">
            {results.image_data.map((image, index) => (
              <div key={index} className="image-result">
                <img
                  src={`data:image/jpeg;base64,${image.base64}`}
                  alt={image.file_name}
                  className="result-image"
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SearchResults;
