import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Remplacez useHistory par useNavigate

const SearchBar = () => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();  // Utilisez useNavigate ici

  const handleSearch = () => {
    if (query.trim() !== '') {
      navigate(`/search/${query}`);  // Utilisez navigate au lieu de history.push
    }
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Rechercher des ressources Ã©ducatives..."
      />
      <button onClick={handleSearch}>Rechercher</button>
    </div>
  );
};

export default SearchBar;
