import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SearchResults.css';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState(null);
  const [query, setQuery] = useState(location.state?.query || ''); // Termes de recherche initial
  const [searchTerm, setSearchTerm] = useState(''); // Termes de recherche actuels pour la nouvelle recherche

  // Fonction pour effectuer la recherche
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      try {
        // Envoi de la nouvelle recherche vers l'API (backend)
        const youtubeApiKey = 'AIzaSyCJsY2i6l0-nYeaNNMCq2ubdhmCSMjQqic'; // Remplacer par votre vraie cl√© API
        const response = await fetch(`http://localhost:5000/process`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            query: query,
            youtube_api_key: youtubeApiKey,
            image_dir: "images/" // Add this if necessary
          }),
        });
        
        const data = await response.json();

        // Naviguer vers la m√™me page pour afficher les nouveaux r√©sultats
        navigate('/results', { state: { query: searchTerm, searchResults: data } });
        console.log('Search results:', data);

      } catch (error) {
        console.error('Erreur lors de la r√©cup√©ration des r√©sultats:', error);
      }
    }
  };

  // R√©cup√©rer les r√©sultats de recherche de la page pr√©c√©dente (ou actualiser avec les nouveaux r√©sultats)
  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
      
    } else {
      // Rediriger vers la page d'accueil si les r√©sultats sont absents
      navigate('/');
    }
  }, [location.state, navigate]);

  // Si les r√©sultats n'ont pas encore √©t√© r√©cup√©r√©s, afficher "Loading..."
  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      {/* Barre de recherche pour une nouvelle recherche */}
      <div className="search-bar-container">
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            type="text"
            placeholder="Search again..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
      </div>

      <h2>{query ? `Search Results for: ${query}` : 'Search Results'}</h2>

      <div className="results-section">
      <div className="results-column">
  <h3>PDF Results</h3>
  <ul className="pdf-list">
    {results.pdf_results.map((result, index) => (
      <li key={index} className="pdf-result-item">
        <a href={result.link} target="_blank" rel="noopener noreferrer" className="pdf-link">
          <span className="pdf-icon">üìÑ</span> {/* Ic√¥ne PDF */}
          {result.title} - {result.authors}
        </a>
      </li>
    ))}
  </ul>
</div>


        {/* R√©sultats Vid√©os */}
        <div className="results-column">
          <h3>Video Results</h3>
          <div className="video-grid">
            {results.video_results.map((video, index) => (
              <div key={index} className="video-item">
                <a href={video.link} target="_blank" rel="noopener noreferrer">
                  <img 
                    src={video.thumbnail} 
                    alt={video.title} 
                    className="video-thumbnail"
                  />
                </a>
                <p>{video.title}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Image Results */}
<div className="results-column">
  <h3>Image Results</h3>
  <div className="image-grid">
    {results.image_keywords &&
      Object.keys(results.image_keywords).map((imageFile, index) => {
        const imageData = results.image_keywords[imageFile];
        return (
          <div key={index} className="image-result">
            <img
              src={imageData.image_url} // Use direct URL instead of Base64
              alt={imageFile}
              className="result-image"
            />
            <p>Keywords: {imageData.keywords.join(", ")}</p>
          </div>
        );
      })}
  </div>
</div>

      </div>
    </div>
  );
};

export default SearchResults;
