import React, { useState } from 'react';
import './SearchBar.css';
import { useNavigate } from 'react-router-dom';

const SearchBar = () => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const handleSearch = async () => {
    if (query.trim()) {
      try {
        const youtubeApiKey = 'AIzaSyCJsY2i6l0-nYeaNNMCq2ubdhmCSMjQqic'; // Replace with your actual YouTube API key
        const response = await fetch(`http://localhost:5000/search?query=${encodeURIComponent(query)}&youtube_api_key=${youtubeApiKey}`);
        const data = await response.json();
        
        // You can pass the search results to the next page or display them in the same page
        console.log('Search results:', data);

        // Assuming you want to navigate to a results page
        navigate(`/results?query=${encodeURIComponent(query)}`, { state: { searchResults: data } });
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    }
  };

  return (
    <div className="search-container">
      <h1 className="main-title">What are you looking for?</h1>
      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="search-input"
          placeholder="Search for education"
        />
        <button onClick={handleSearch} className="search-button">
          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth="1.5" stroke="currentColor" className="size-6">
            <path strokeLinecap="round" strokeLinejoin="round" d="m21 21-5.197-5.197m0 0A7.5 7.5 0 1 0 5.196 5.196a7.5 7.5 0 0 0 10.607 10.607Z" />
          </svg>
        </button>
      </div>
    </div>
  );
};

export default SearchBar;
