import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './SearchResults.css';

const SearchResults = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [results, setResults] = useState(null);
  const [query, setQuery] = useState(location.state?.query || ''); // To store search query
  const [searchTerm, setSearchTerm] = useState(''); // To capture new search

  // Handle form submission for new search
  const handleSearchSubmit = async (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate('/search', { state: { query: searchTerm } }); // Navigate to the search page with new term
    }
  };

  // Retrieve search query and results from the state passed via React Router
  useEffect(() => {
    const searchResults = location.state?.searchResults;
    if (searchResults) {
      setResults(searchResults);
    } else {
      navigate('/');
    }
  }, [location.state, navigate]);

  if (!results) {
    return <div>Loading...</div>;
  }

  return (
    <div className="search-results-container">
      {/* Nouvelle barre de recherche */}
      <div className="search-bar-container">
        <form onSubmit={handleSearchSubmit} className="search-form">
          <input
            type="text"
            placeholder="Search again..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          <button type="submit" className="search-button">Search</button>
        </form>
      </div>

      <h2>Search Results for: {query}</h2>

      <div className="results-section">
        {/* PDF Results */}
        <div className="results-column">
          <h3>PDF Results</h3>
          <ul>
            {results.pdf_results.map((result, index) => (
              <li key={index} className="pdf-result-item">
                <div className="pdf-preview">
                  <a href={result.link} target="_blank" rel="noopener noreferrer" className="pdf-preview-link">
                    <img
                      src="https://www.iconfinder.com/data/icons/picto-foundry-document/32/PDF_file-512.png" // Example PDF Icon
                      alt="PDF Preview"
                      className="pdf-icon"
                    />
                    <span>View PDF</span>
                  </a>
                </div>
                <div className="pdf-details">
                  <h4>{result.title}</h4>
                  <p>{result.authors}</p>
                  <span>{result.size} | {result.page_count} pages</span>
                </div>
                <div className="pdf-actions">
                  <button className="download-button">
                    <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" className="download-icon">
                      <path strokeLinecap="round" strokeLinejoin="round" d="M15 10l4 4m0 0l-4 4m4-4H3" />
                    </svg>
                    Download
                  </button>
                </div>
              </li>
            ))}
          </ul>
        </div>

        {/* Other Results (Video, Image, etc.) */}
        {/* Add similar logic here for video/image results */}
      </div>
    </div>
  );
};

export default SearchResults;
